import { Component } from '@angular/core';
import { TableroService } from '../service/Tablero'; 


@Component({
    selector: 'app-tablero',
    templateUrl: 'game.component.html',
  })


export class TableroComponent {

  tab: string[][];
  
  constructor(private tableroService: TableroService) {
    this.tab = tableroService.tablero;
  }
  public realizarMovimiento(fila: number, columna: number, jugador: string): boolean {
    if (fila < 0 || fila >= 3 || columna < 0 || columna >= 3 || this.tab[fila][columna] !== " ") {
        return false;
    }

    this.tab[fila][columna] = jugador;
    return true;
}



}
