import { Tablero } from "../Models/Tablero";

export class TableroService {

    public tablero: string[][] = [
        [" ", " ", " "],
        [" ", " ", " "],
        [" ", " ", " "]
    ];

    mostrarTablero(): void {
        console.log("Tablero:");
        for (const fila of this.tablero) {
            console.log(fila.join(" | "));
        }
    }   

    public realizarMovimiento(fila: number, columna: number, jugador: string): boolean {
        if (fila < 0 || fila >= 3 || columna < 0 || columna >= 3 || this.tablero[fila][columna] !== " ") {
            return false;
        }
    
        this.tablero[fila][columna] = jugador;
        return true;
    }
}
